package Ex3;

public class AppMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 30;
		
		double n = 7.5;
		double m = 3.4;
		
		System.out.println("Valor de las variables:");
		System.out.println("X =" + x);
		System.out.println("Y =" + y);
		System.out.println("N =" + n);
		System.out.println("M =" + m);
		
		System.out.println(" ");
		
		System.out.println("Suma de X+Y: " + (x+y));
		System.out.println("Resta de X-Y: " + (x-y));
		System.out.println("Multiplicación de X*Y: " + (x*y));
		System.out.println("División de X/Y: " + (x/y));
		System.out.println("Módulo de X%Y: " + (x%y));
		
		System.out.println(" ");
		
		System.out.println("Suma de N+M: " + (n+m));
		System.out.println("Resta de N-M: " + (n-m));
		System.out.println("Multiplicación de N*M: " + (n*m));
		System.out.println("División de N/M: " + (n/m));
		System.out.println("Módulo de N%M: " + (n%m));
		
		System.out.println(" ");
		
		System.out.println("Suma de X+N: " + (x+n));
		System.out.println("División de Y/M: " + (y/m));
		System.out.println("Módulo de Y%M: " + (y%m));
		
		System.out.println(" ");
		
		System.out.println("El doble de cada variable:");
		
		System.out.println("Doble de X: " + (x*2));
		System.out.println("Doble de Y: " + (y*2));
		System.out.println("Doble de N: " + (n*2));
		System.out.println("Doble de M: " + (m*2));
		
		System.out.println(" ");
		
		System.out.println("Suma total de varibles: " + (x+y+n+m));
		System.out.println("Multiplicación total de varibles: " + (x*y*n*m));

	}

}
