package Ex4;

public class AppMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		
		//Incrementar N en 77
		n = n + 77;
		System.out.println("N + 77: "+n);
		
		//Decrementar N en 3
		n = n - 3;
		System.out.println("N - 3: "+n);
		
		//Duplicar el valor de N
		n = n*2;
		System.out.println("N * 2: "+n);
	}

}
